import React, { useState } from "react";
import axios from "axios";
import "./../assets/styles/SearchComponent.css";

const SearchComponent = ({ entity, onSearchResults }) => { // Accept onSearchResults as prop
    const [title, setTitle] = useState("");
    const [results, setResults] = useState([]); // Corrected state declaration

    const handleSearch = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.get(`http://localhost:3004/api/search/${entity}`, {
                params: {
                    title,
                }
            });
            setResults(response.data);
            onSearchResults(response.data); // Call the prop function to pass results to Dashboard
        }
        catch (err) {
            console.log("Search Failed:", err);
        }
    };

    return (
        <div className="search-container">
            <h2>Search {entity}</h2>
            <form className="search-form" onSubmit={handleSearch}>
                <input 
                    className="search-input" 
                    type="text" 
                    placeholder={`Search ${entity}...`} // Fixed placeholder syntax
                    value={title} 
                    onChange={(e) => setTitle(e.target.value)} 
                />
                <button className="search-button" type="submit">Search</button>
            </form>
            <div>{results}</div>
        </div>
    );
};

export default SearchComponent;
