import React, { useEffect, useState } from 'react';
import { fetchToken } from '../components/Auth/auth';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import SearchComponent from './SearchComponent';


import '../assets/styles/dashboard.css'; // Import CSS file for Dashboard page

const Dashboard = () => {
    const navigate = useNavigate();
    console.log("Dashboard request recieved");
    const [tasks, setTasks] = useState([]);
    // const [filteredTasks, setFilteredTasks] = useState([]);

    useEffect(() => {
        const fetchTasks = async () => {
            try {
                const token = await fetchToken();
                console.log('token is', token);                
                const response = await fetch('http://localhost:3003/api/tasks', {
                    headers: {
                        'x-auth-token': token,
                    }
                });
                if (!response.ok) {
                    throw new Error('Failed to fetch tasks');
                }
                const data = await response.json();
                console.log("task data", data);
                setTasks(data);
                // setFilteredTasks(data);
            } catch (err) {
                console.error('Fetch tasks error', err);
            }
        };

        fetchTasks();
    }, []);
    const handleViewSubTasks = async(taskId) => {
        try {
            const token = await fetchToken();
            console.log("token is", token);
            navigate(`/subtasks/${taskId}`, {
                state: {token}
            });
        }
        catch(err) {
            console.log('Failed to fetch token', err);
        }
    };
    const handleSearchResults = (results) => {
        // Handle cases where results are empty or undefined
        if (!results || !Array.isArray(results)) {
            console.log('Invalid or no search results');
            setTasks([]);
            return;
        }
        
        // Filter valid tasks with _id and title
        const validTasks = results.filter(task => task._id && task.title);
        console.log('Valid search results:', validTasks);
        setTasks(validTasks);
    };   
    
    return (
        <div className="dashboard-container">          
            <h1>Dashboard</h1>
            <p><Link to="/task" className="home-link-button">Create Task</Link></p>
            <p><Link to="/subtask" className="home-link-button">Create Sub Task</Link></p>
            <SearchComponent entity="tasks" onSearchResults={handleSearchResults} /> {/* Include SearchComponent */}
            <div className="tasks-table">
                <h2>Tasks</h2>
                    <table>
                        <thead>
                            <tr>
                                <th>Title</th>
                                <th>Description</th>
                                <th>Status</th>
                                <th>Priority</th>
                                <th>SubTasks</th>
                            </tr>
                        </thead>
                        <tbody>
                            {Array.isArray(tasks) && tasks.length > 0 ? (
                                tasks.map((task) => (
                                    <tr key={task._id || Math.random()}>
                                        <td>{task.title || "Untitled"}</td>
                                        <td>{task.description || "No description available"}</td>
                                        <td>{task.status || "Unknown"}</td>
                                        <td>{task.priority || "Not specified"}</td>
                                        <td>
                                            <button onClick={() => handleViewSubTasks(task._id)} className='subtasks-link'>View SubTasks</button>
                                        </td>
                                    </tr>
                                ))
                            ) : (
                                <tr>
                                    <td colSpan="5">No tasks available</td>
                                </tr>
                            )}
                    </tbody>
                    </table>
            </div>
        </div>
    );
};

export default Dashboard;
