// Subtask.js
import React, {useState} from 'react';
import axios from "axios";
import { useNavigate } from 'react-router-dom';
import '../assets/styles/subtask.css';

const SubTaskForm = () => {
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        status: 'pending',
        dueDate: '',
        parentTaskId: '',
        createdBy: '',
    });
    const navigate = useNavigate();

    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    const handleInputChange = (e) => {
        const {name, value} = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:3002/api/subtasks', formData);
            setSuccess('Subtask created successfully.');
            setError('');
            navigate('/Dashboard');
        }
        catch(err) {
            setError('Failed while creating subtask');
            setSuccess('');
        }
    };
    return (
        <div className='form-container'>
            <h2 className='form-title'>Create SubTask</h2>
            {error && <p className='error-message'>{error}</p>}
            {success && <p className='success-message'>{success}</p>}
            <form onSubmit={handleSubmit} className='subtask-form'>
                <div className='form-group'>
                    <label>Title</label>
                    <input type='text' name='title' value={formData.title} onChange={handleInputChange} required />
                </div>
                <div className='form-group'>
                    <label>Description</label>
                    <textarea name='description' value={formData.description} onChange={handleInputChange} required></textarea>
                </div>
                <div className='form-group'>
                    <label>Status</label>
                    <select name='status' value={formData.status} onChange={handleInputChange}>
                        <option value='pending'>Pending</option>
                        <option value='in-progress'>In Progress</option>
                        <option value='completed'>Completed</option>
                    </select>
                </div>
                <div className='form-group'>
                    <label>Due date</label>
                    <input type='date' name='dueDate' value={formData.dueDate} onChange={handleInputChange} required />
                </div>
                <div className='form-group'>
                    <label>Parent Task ID</label>
                    <input type='text' name='parentTaskId' value={formData.parentTaskId} onChange={handleInputChange} required />
                </div>
                <div className='form-group'>
                    <label>Created By</label>
                    <input type='text' name='createdBy' value={formData.createdBy} onChange={handleInputChange} />
                </div>
                <button type='submit' className='submit-button'>Create SubTask</button>
            </form>
        </div>
    );
};
export default SubTaskForm;
