// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './pages/Home';
import Login from './components/Auth/Login';
import Signup from './components/Auth/Register';
import Dashboard from './pages/Dashboard';
import TaskForm from './pages/Task';
import SubTaskForm from './pages/Subtask';

import './App.css'; // General styles for your app

function App() {
    return (
        <Router>
            <div className="app-container">
                <Routes>
                    <Route path="/" element={<Home />} />
                    <Route path="http://localhost:3001/login" element={<Login />} />
                    <Route path="http://localhost:3001/signup" element={<Signup />} />
                    <Route path="http://localhost:3003/dashboard" element={<Dashboard />} />
                    <Route path="http://localhost:3003/task" element={<TaskForm />} />
                    <Route path="http://localhost:3002/subtask" element={<SubTaskForm />} />
                </Routes>
            </div>
        </Router>
    );
}

export default App;
