// SearchComponent.js
import React, {useState} from "react";
import axios  from "axios";
import "./../assets/styles/SearchComponent.css";

const SearchComponent = ({entity}) => {
    const [query, setQuery] = useState("");
    const [results, setResults] = useState([]);

    const handleSearch = async(e) => {
        e.preventDefault();
        try {
            const response = await axios.get(`http://localhost:3004/api/search/${entity}`, {
                params: {
                    query,
                }
            });
            setResults(response.data);
        }
        catch(err) {
            console.log("Search Failed:", err);
        }
    };
    return (
        <div className="search-container">
            <h2>Search {entity}</h2>
            <form className="search-form" onSubmit={handleSearch}>
                <input className="serach-input" type="text" placeholder="{`Search ${entity}... `}" value={query} onChange={(e) => setQuery(e.target.value)} />
                <button className="search-button" type="submit">Search</button>
            </form>
        </div>
    );
};

export default SearchComponent;
