import React, { useState } from "react";
import axios from "axios";
import "./../assets/styles/SearchComponent.css";

const SearchComponent = ({ entity, onSearchResults }) => { // Accept onSearchResults as prop
    const [title, setTitle] = useState("");
    const [results, setResults] = useState([]); // Corrected state declaration

    const handleSearch = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.get(`http://localhost:3004/api/search/${entity}`, {
                params: { title },
            });
            console.log('Search response data:', response.data); // Log search results
            setResults(response.data);
            onSearchResults(response.data); // Call the parent function to pass results to Dashboard
        } catch (err) {
            console.log("Search Failed:", err);
        }
    };
    
    return (
        <div className="search-container">
            <h2>Search {entity}</h2>
            <form className="search-form" onSubmit={handleSearch}>
                <input 
                    className="search-input" 
                    type="text" 
                    placeholder={`Search ${entity}...`} 
                    value={title} 
                    onChange={(e) => setTitle(e.target.value)} 
                />
                <button className="search-button" type="submit">Search</button>
            </form>
            
            {/* Check if results is an array and map over it */}
            {Array.isArray(results) && results.length > 0 ? (
                <div className="results-container">
                    {results.map((result) => (
                        <div key={result._id}>
                            <p>Title: {result.title}</p>
                            <p>Description: {result.description}</p>
                            {/* Render other fields as needed */}
                        </div>
                    ))}
                </div>
            ) : (
                <p>No results found</p> // Add fallback message when no results
            )}
        </div>
    );    
};

export default SearchComponent;
